$(document).ready(function(){

	$("#header").parallax();
	$("#about").parallax();

	$(".navbar-nav li a, .menus").on("click",function(e){var t=$(this);
		$("html, body").stop().animate({scrollTop:$(t.attr("href")).offset().top},1500,"easeInOutExpo");e.preventDefault()});
	$("body").scrollspy({target:".navbar-fixed-top"});
	$(".navbar-collapse ul li a").click(function(){$(".navbar-toggle:visible").click()});

    // Email form

    $('form#messagecontact').submit(function(e){
    	e.preventDefault();
    	$.post($(this).attr('action'),$(this).serialize(),function(data){
    		$('div#messages').html(data);
    	});
    });

	$('#toggleButton').click(function() {
        $('html').attr('lang', function(index, attr) {
            return attr === 'ar' ? 'en' : 'ar';
        });

        $('body').attr('dir', function(index, attr) {
            return attr === 'rtl' ? 'ltr' : 'rtl';
        });

        $('#pageTitle').text(function(index, text) {
            return text === 'مرحبًا بك في موقعي' ? 'Welcome to My Website' : 'مرحبًا بك في موقعي';
        });

        $('#content').text(function(index, text) {
            return text === 'هذا هو المحتوى العربي' ? 'This is the English content' : 'هذا هو المحتوى العربي';
        });
    });
	
	

});
